package string;
import java.util.*;
public class FindIfSebsequence {
    public static boolean isSubsequence(String s, String t) {
        return isSubsequence(s.toCharArray(), t.toCharArray());
    }

    public static boolean isSubsequence(char[] stringS, char[] stringT){

        if(stringS.length > stringT.length){
            return false;
        }

        if(stringS.length == 0){
            return true;
        }

        for(int i=0; i<stringT.length ; i++){
            if(stringS[0] == stringT[i]){
                return true && isSubsequence(Arrays.copyOfRange(stringS, 1, stringS.length), Arrays.copyOfRange(stringT, i+1, stringT.length));
            }
        }
        return false;
    }

    public static void charCount(String s){
        char[] array = s.toCharArray();
        HashMap<Character, Integer> hashmap = new HashMap<>();
        for(Character letter : array){
            if(hashmap.containsKey(letter)){
                hashmap.put(letter, hashmap.get(letter)+1);
            }
            else{
                hashmap.put(letter, 1);
            }
        }

        for(Map.Entry<Character, Integer> entry: hashmap.entrySet()){
            System.out.println(entry.getKey() + "'s count is : "+ entry.getValue());
        }
    }
    public static void main(String[] args) {
        String s1 = "leeeeetcode";
        String s2 = "";
        System.out.println("String 1");
        charCount(s1);
        System.out.println("String 2");
        charCount(s2);
        boolean answer = isSubsequence(s1, s2);
        System.out.println(answer);
    }
}
